import React from 'react';
import styled from 'styled-components';
import Layout from '../components/Layout/Layout';
import RTracker from "../components/RenderProps/RegularTracker/RTracker";
import ImageTracker from "../components/RenderProps/RegularTracker/ImageTracker";
import Tracker from "../components/RenderProps/Tracker/Tracker";


const Section = styled.section`
    height: inherit;
    float: left;
    text-align: center;
    font-family: 'Roboto', sans-serif;
    font-variant-caps: all-small-caps; 
    position: relative;
    width: 100%;
`;

const Image = styled.img`
    position: absolute;
    height: 50px;
    width: 50px;
`;

const Components = () => {
    return (
        <Layout page="Components">
            <Section>
                <h2>Regular component</h2>
                <RTracker/>
                <ImageTracker/>
            </Section>
            <Section>
                <h2>Component with Render props</h2>
                <Tracker>{t => <Image src="./src/images/cat.png"
                                      style={{left: t.x, top: t.y}}/>}</Tracker>
                <Tracker>{t => <Image src="./src/images/dog.png"
                                      style={{left: t.x, top: t.y}}/>}</Tracker>
                <Tracker>{t => <h1>{t.x} | {t.y}</h1>}</Tracker>
            </Section>
        </Layout>
    )
};

export default Components;
