import React, {useState} from 'react';
import styled from "styled-components";

type imageProp = {
    image?: any
    x?: number
    y?: number
}

const Section = styled.section`
    min-height: 350px;
    margin: 50px 0;
    padding: 0;
    position: relative;
    width: calc(100% - 50px);
    z-index: 1;
`;

const ImageWrapper = styled.img`
    position: absolute;
    height: 50px;
    width: 50px;
`;

const ImageTracker: React.FC<imageProp> = (props) => {
    const [state, updateState] = useState({x: 0, y: 0});

    let handleMouseMove = (e: any) => {
        updateState({
            x: e.clientX,
            y: e.clientY
        })
    };

    return <Section onMouseMove={handleMouseMove}>
        <ImageWrapper src={props.image} style={{left: state.x, top: state.y}}/>
    </Section>
};

ImageTracker.defaultProps = {
    image: "/src/images/cat.png"
};

export default ImageTracker;
