import React from 'react';
import styled from 'styled-components';
import Disabled from '../hoc/Disable/Disable';

type figureProps = {
    imgSource?: string,
    caption?: string
}

const Fig = styled.figure`
    float:left;
    margin: 10px;
    width: 250px;
    img {
        border-radius: 3px;
        width: 100%;
    },
    figcaption {
        font-size: 12px;
        text-align:center;
    }
`;

const Figure: React.FC<figureProps> = (props) => {
    return <Fig>
        <img src={props.imgSource}/>
        <figcaption>{props.caption}</figcaption>
    </Fig>
};

Figure.defaultProps = {
    imgSource: '/src/images/image.jpg',
    caption: 'please insert caption or remove it by adding blocked'
};


export default Disabled(Figure);
