import React, {useState} from 'react';
import styled, {ThemeProvider} from 'styled-components';
import {string} from "prop-types";

const theme = {
    colors: {
        primary: `yellow`,
        secondary: `red`,
    }
};

type ButtonProps = {
    class?: string,
    name?: string,
    children?: string,
    value?: string,
    onclick?: {},
    kind?: string[]
}

const MainButton = styled.button`
  background-color: ${props => props.theme.colors.primary};
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const buttonClick = () => {
    console.log('click')
};

const Button: React.FC<ButtonProps> = (props) => {
    [addClass, setClass] = useState();
    return <ThemeProvider theme={theme}>
        <MainButton className={addClass} name={props.name} value={props.value} onClick={buttonClick}>{props.children}</MainButton>
    </ThemeProvider>
};

Button.defaultProps = {
    name: 'button',
    value: 'Click',
    children: 'Click button'
};

export default Button;
