import React, {useState} from 'react';
import styled from 'styled-components';
import InputField from '../../../components/InputField/InputField';
import Button from '../../../components/Button/Button';

type contactFormProps = {
    action?: string
    class: string
    title: string
    mail?: string
    viewClass: string
}

const Form = styled.form`
    width: inherit;
`;

const Textarea = styled.textarea`
    height: 100px;
    border: 1px solid #eee;
    border-radius: 3px;
    font-size: 16px;
    margin: 10px 0;
    padding: 10px 5px;
    outline: none;
    resize: none;
    width: 250px;
`;

const Span = styled.span`
    display: none;

    &.view {
        display: block;
    }
`;

const ContactForm: React.FC<contactFormProps> = (props) => {
    const [view, setView] = useState(props.viewClass);
    return <React.Fragment>
        <Form className={props.class}>
            <fieldset>
                <legend>{props.title}</legend>
                <div>
                    <label>Name:</label>
                    <InputField name="name" type="name" maxValue={50}></InputField>
                </div>
                <div>
                    <label>Email:</label>
                    <InputField name="email" type="email" placeholder="Email" maxValue={25}></InputField>
                </div>
                <div>
                    <label>Message:</label>
                    <Textarea placeholder="Message"></Textarea>
                </div>
                {/*{sendMail => sendMail.input ? <p>{sendMail.input}</p> : <p>Send mail</p>*/}
                {/*}*/}
            </fieldset>
        </Form>
        <Button type="button" onClick={() => setView('view')}>Send</Button>
        <Span className={view}>this is viewed when clicked</Span>
    </React.Fragment>
};

ContactForm.defaultProps = {
    action: '/components',
    class: 'contact-form'
};

export default ContactForm;
