import React from 'react';
import styled from 'styled-components';

type figureProps = {
    imgSource?: string
    caption?: string
    type: string
}

const Fig = styled.figure`
    border-radius: 3px;
    float:left;
    margin: 10px;
    width: 250px;
    img {
        border-radius: 3px;
        width: 100%;
    },
    figcaption {
        font-size: 12px;
        text-align:center;
    }
`;

const MedFig = styled(Fig)`
    width: 400px
`;

const LargeFig = styled(Fig)`
    border: 3px solid #d0245a;
`;

const XlargeFig = styled(Fig)`
    border: 3px solid #237c7e;
`;

const Figure: React.FC<figureProps> = (props) => {
    let showFigure = {};

    switch (props.type) {
        case('primary'): {
            showFigure = <Fig>{props.caption} | primary</Fig>;
        }
            break;
        case('secondary'): {
            showFigure = <MedFig>{props.caption} | secondary</MedFig>;
        }
            break;
        case('third'): {
            showFigure = <LargeFig>{props.caption} | third</LargeFig>;
        }
            break;
        case('forth'): {
            showFigure = <XlargeFig>{props.caption} | forth</XlargeFig>;
        }
            break;
        default:
            showFigure = {};
    }

    return <>{showFigure}</>
};

Figure.defaultProps = {
    imgSource: '/src/images/image.jpg',
    caption: 'please insert caption or remove it by adding blocked'
};


export default Figure;
