import React from 'react';
import styled from 'styled-components';
import Layout from '../components/Layout/Layout';
import Image from '../components/RenderProps/Image/Image';
import RegularTracker from "../components/RenderProps/RegularTracker/RegularTracker";
import DisplayTracker from "../components/RenderProps/RegularTracker/DisplayTracker";
import Tracker from "../components/RenderProps/Tracker/Tracker";
import Display from "../components/RenderProps/Display/Display";

const Section = styled.section`
    height: inherit;
    float: left;
    text-align: center;
    font-family: 'Roboto', sans-serif;
    font-variant-caps: all-small-caps; 
    position: relative;
    width: calc(100% - 50px);
`;


const Components = () => {
    const data = [
        {
            id: 1,
            name: "Aruba",
            currency: "FL"
        },
        {
            id: 2,
            name: "Brazil",
            currency: "BRL"
        },
        {
            id: 3,
            name: "England",
            currency: "POUND"
        },
        {
            id: 4,
            name: "Japan",
            currency: "YEN"
        }
    ];
    return (
        <Layout page="Components">
            <Section>
                <h2>Regular component</h2>
                    <RegularTracker image="./src/images/dog.png"/>
                    <DisplayTracker/>
            </Section>
            <Section>
                <h2>Component with design pattern</h2>
                    <Tracker>{t => (<Image tracker={t}/>)}</Tracker>
                    <Tracker>{t => (<Image image="./src/images/dog.png" tracker={t}/>)}</Tracker>
                    <Tracker>{t => <Display tracker={t}/>}</Tracker>
            </Section>
        </Layout>
    )
};

export default Components;
