import React, {useState} from 'react';
import styled, {StyledFunction} from 'styled-components';

type ButtonProps = {
    addClass?: string
    myClass?: string
    name?: string
    children?: string
    value?: string
    onclick?: {}
    kind?: string[]
}

function styledComponentWithProps<T, U extends HTMLElement = HTMLElement>(styledFunction: StyledFunction<React.HTMLProps<U>>): StyledFunction<T & React.HTMLProps<U>> {
    return styledFunction
}

interface MyProps {
    // ...
}

const MainButton = styledComponentWithProps<MyProps, HTMLDivElement>(styled.button);

const Button: React.FC<ButtonProps> = (props) => {
    const [addClass, setClass] = useState(props.myClass);

    const buttonClick = () => {
        setClass('click');
    };

    return <MainButton className={addClass} name={props.name} value={props.value} onClick={buttonClick}>{props.children}</MainButton>
};

Button.defaultProps = {
    myClass: 'button',
    name: 'button',
    value: 'Click',
    children: 'Click button'
};

export default Button;
