import React from 'react';
import addClass from '../hoc/AddClass/AddClass';

type paginationProps = {
    pages: number
    class?: string
    totalPages: number
};

const Pagination = (props: paginationProps) => {
    let totalPages = props.pages;
    let PagesReturn: any[];
    const amountPages = () => {
        for (let i = 0; i < totalPages; i++) {
            PagesReturn.push(<button>{[i]}</button>);
        }
        return PagesReturn;
    };
    return <React.Fragment>
        <button>Previous</button>
        {amountPages()}
        <button>Next</button>
    </React.Fragment>
};

export default addClass(Pagination, 'pagination');
