import React from 'react';
import styled from 'styled-components';

type jumboProps = {
    header?: string;
}

const Content = styled.section`
    background-color: #82BFE0;
    float: left;
    height: calc(500px - 80px); //this equals 500px
    padding: 40px 20px;
    text-align: left;
    width: calc(100% - 40px);

    h3{
    },
    p {
      font-size: 18px;
    }
`;

const Jumbo: React.FC<jumboProps> = (props) => {
    return <Content>
        <h3>{props.header}</h3>
        <p>{props.children}</p>
        </Content>
};

Jumbo.defaultProps = {
    header: 'Header Jumbo'
};

export default Jumbo;
