import React, {useState} from 'react';
import styled from 'styled-components';

// const theme = {
//     colors: {
//         primary: `yellow`,
//         secondary: `red`,
//     }
// };

type ButtonProps = {
    addClass?: string
    myClass?: string
    name?: string
    children?: string
    value?: string
    onclick?: {}
    kind?: string[]
}

const MainButton = styled.button`
  background-color: ${props => `${props.theme.primary}`};
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &.red {
    background-color: red;
    }

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const Button: React.FC<ButtonProps> = (props) => {
    const [addClass, setClass] = useState(props.myClass);

    const buttonClick = () => {
        setClass('red');
    };

    return <MainButton className={addClass} name={props.name} value={props.value} onClick={buttonClick}>{props.children}</MainButton>
};

Button.defaultProps = {
    myClass: 'button',
    name: 'button',
    value: 'Click',
    children: 'Click button'
};

export default Button;
