import React from 'react';
import styled from 'styled-components';
import Layout from '../components/Layout/Layout';
import Image from '../components/RenderProps/Image/Image';
import RTracker from "../components/RenderProps/RegularTracker/RTracker";
import ImageTracker from "../components/RenderProps/RegularTracker/ImageTracker";
import Tracker from "../components/RenderProps/Tracker/Tracker";


const Section = styled.section`
    height: inherit;
    float: left;
    text-align: center;
    font-family: 'Roboto', sans-serif;
    font-variant-caps: all-small-caps; 
    position: relative;
    width: 100%;
`;

const Components = () => {
