    //     },
    //     {
    //         id: 4,
    //         name: "Japan",
    //         currency: "YEN"
    //     }
    // ];
    return (
        <Layout page="Components">
            <Section>
                <h2>Regular component</h2>
                    <RTracker/>
                    <ImageTracker/>
            </Section>
            <Section>
                <h2>Component with Render props</h2>
                    <Tracker>{t => <img src="./src/images/dog.png" style={{left: t.x, top: t.y}}/>}</Tracker>
                <Tracker>{t => <h1>{t.x} | {t.y}</h1>}</Tracker>
            </Section>
        </Layout>
    )
};

export default Components;
