import React, {useState, ChangeEvent} from 'react';
import styled from 'styled-components';

type InputFieldType = {
    qty: any,
    name: string,
    placeholder?: string,
    type: string,
}

const Input = styled.input`
    border: 1px solid #eee;
    border-radius: 3px;
    font-size: 16px;
    padding: 10px 5px;
    outline: none;
    width: 250px;
`;


const InputField: React.FC<InputFieldType> = (props) => {
    const initialQty = parseInt(props.qty);

    const [qty, setQty] = useState<number | ''>(0);

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value;
        if (value == '') {
            setQty('');
        }
        if (parseInt(value) >= 1) {
            console.log("nada");
        }
    };

    const handleBlur = () => {
        if (!qty) {
            setQty(initialQty);
        }
    };

    return <Input name={props.name} type={props.type} placeholder={props.placeholder} onChange={e => handleChange(e)} onBlur={() => handleBlur()}/>
};

InputField.defaultProps = {
    name: 'input name',
    placeholder: '',
    type: 'name'
};

export default InputField;
