import React, {useState} from 'react';
import styled from "styled-components";

type imageProp = {
    x: number
    y: number
}

const Section = styled.section`
    bottom: 0;
    margin: 50px 0;
    left: 0;
    padding: 0;
    position: relative;
    width: calc(100% - 50px);
`;

const RTracker: React.FC<imageProp> = () => {
    const [state, updateState] = useState({x: 0, y: 0});

    let handleMouseMove = (e: any) => {
        updateState({
            x: e.clientX,
            y: e.clientY
        })
    };

    return <Section onMouseMove={handleMouseMove}>
      <h2>{state.x} | {state.y}</h2>
    </Section>
};

export default RTracker;
