import React from 'react';
import styled from 'styled-components';

type blockQuoteProps = {
    content?: string;
}

const BlockquoteContent = styled.blockquote`
    float: left;
    font-size: 42px;
    font-style: italic;
    text-align: center;
    width: 100%;

    &:after {
        content: '"';
    }
    &:before {
        content: '"';
    }
`;

const Blockquote: React.FC<blockQuoteProps> = (props) => {
    return <BlockquoteContent>{props.content}</BlockquoteContent>
};

Blockquote.defaultProps = {
    content: 'This is the Blockquote content'
};

export default Blockquote;
