import React from 'react';
import styled from 'styled-components';
import theme from "styled-theming";

type Variants = "default" | "primary" | "cta"

type blockQuoteProps = {
    content?: string
    children?: string
    variant?: Variants
}

const blockColors = theme.variants("mode", "variant", {
    default: {color: "#82BFE0"},
    primary: {color: "#d0245a"},
    cta: {color: "#237c7e"}
});

const BlockquoteContent = styled.blockquote<blockQuoteProps>`
    color: ${blockColors};
    float: left;
    font-size: 42px;
    font-style: italic;
    line-height: 42px;
    margin: 10px auto;
    text-align: center;
    width: calc(100% - 40px);

    &:after {
        content: '"';
    }
    &:before {
        content: '"';
    }
`;

const Blockquote = (props: blockQuoteProps) => {
    let curContent;
        if (props.children) {
            curContent = <>{props.children}</>;
        } else {
            curContent = <>{props.content}</>;
        }

    return <BlockquoteContent variant="primary">{curContent}</BlockquoteContent>
};

Blockquote.defaultProps = {
    content: "Blockquote content",
    variant: "default"
};

export default Blockquote;
