import React from 'react';
import styled from 'styled-components';
import {Ingredients, ingredientProps} from './ingredients/ingredients';

const PizzaContent = styled.div`
    float: right;
    text-align: center;
    width: 250px;
`;

const pizza = (props: ingredientProps) => {
    const transIngredients = Object.keys(props.type)
        .map(i => {
            return [...Array(props.type[i])].map((_, x) => {
                return <Ingredients key={i + x} type={i}/>
            });
        });
    return <PizzaContent>
        <Ingredients type="ham"/>
        {transIngredients}
        <Ingredients type="mushroom"/>
    </PizzaContent>
};

export default pizza;
