import React from 'react';

type DisableComponent<T> = React.ElementType<T>;

export const Disabled = function<T>(DisableComponent: DisableComponent<T>): DisableComponent<T> {
    return (props) =>
        <DisableComponent className="disabled" {...props}>{props.children}</DisableComponent>
};
