import React, {useState} from 'react';
import styled, {StyledFunction} from 'styled-components';

type ButtonProps = {
    addClass?: string
    myClass?: string
    name?: string
    children?: string
    value?: string
    onclick?: {}
    kind?: string[]
}

type MyProps = {
    primary: boolean
    cta: boolean
    transparent: boolean
};

const styledProps: StyledFunction<MyProps & React.HTMLProps<HTMLInputElement>> = styled.button;

const MainButton = styledProps`
    background-color: ${props => props.primary}

`;

const Button: React.FC<ButtonProps> = (props) => {
    const [addClass, setClass] = useState(props.myClass);

    const buttonClick = () => {
        setClass('click');
    };

    return <MainButton className={addClass} name={props.name} value={props.value} onClick={buttonClick}>{props.children}</MainButton>
};

Button.defaultProps = {
    myClass: 'button',
    name: 'button',
    value: 'Click',
    children: 'Click button'
};

export default Button;
