import React, {useState} from 'react';

type handleChangeProps = {
    handleChange?(): boolean
    handleBlur?(): boolean
};

const HanChanges: React.FC<handleChangeProps> = () => {
    const [input, setInput] = useState('');

    const handleChange = (e: any) => {
        setInput(e.target.value);
        if (e.target.value.length == 20) {
            console.log("almost reaching limit");
        }
    };

    const handleBlur = () => {
        console.log('blur');
    };

    return <>
        {handleChange}
        {handleBlur}
        {input}
    </>
};

export default HanChanges;
