import React from 'react';
import styled from 'styled-components';

type contentProps = {
    header: string,
    content: string,
    children: string
}

const Section = styled.section`
    height: 100%;
    width: 100%;
`;

const Content:React.FC<contentProps> = (props) => {
    return <Section>
        <h2>{props.header}</h2>
        <p>{props.content}</p>
        {props.children}
    </Section>
};

Content.defaultProps = {
    header: 'This is the header of the content',
    content: 'content'
};

export default Content;
