import React from 'react';
import Layout from '../components/Layout/Layout';
import Pizza from '../components/Pizza/Pizza';
import Controls from "../components/Pizza/Controls/Controls";


const Playground:React.FC = () => {
    let state = {
      ingredients: {
          crust:0,
          sauce:0,
          ham: 0,
          cheese: 0,
          pepperoni: 0,
          mushroom: 0
      }
    };
   function addIngredientHandler (type: string){
        const oldCount = state.ingredients[type];
        const updatedCount = oldCount + 1;
        const updatedIngredients = {
            ...state.ingredients
        };
        updatedIngredients[type] = updatedCount;
    }

    let removeIngredientHandler = (type) => {
        const oldCount = state.ingredients[type];
        if (oldCount <= 0) {
            return
        }
        const updatedCount = oldCount - 1;
        const updatedIngredients = {
            ...state.ingredients
        };
        updatedIngredients[type] = updatedCount;
    };

    return (
        <Layout page="Playground">
            <Controls ingredientAdded={addIngredientHandler} ingredientRemove={removeIngredientHandler}/>
            <Pizza ingredients={state.ingredients}/>
        </Layout>
    )
};

export default Playground;
